#include <chrono>
#include <limits>
#include <iomanip>
#include <iostream>
#include <string>

#include <boost/multiprecision/complex_adaptor.hpp>
#include <boost/multiprecision/cpp_complex.hpp>
#include <boost/math/constants/constants.hpp>
#include <boost/multiprecision/cpp_bin_float.hpp>
#include <boost/multiprecision/cpp_dec_float.hpp>
#include <boost/multiprecision/cpp_int.hpp>
#include <boost/math/complex.hpp>

namespace boost_gsoc2020 {
template <class clock_type>
struct stopwatch
{
 public:
   typedef typename clock_type::duration duration_type;

   stopwatch() : m_start(clock_type::now()) {}

   stopwatch(const stopwatch& other) : m_start(other.m_start) {}

   stopwatch& operator=(const stopwatch& other)
   {
      m_start = other.m_start;
      return *this;
   }

   ~stopwatch() {}

   duration_type elapsed() const
   {
      return (clock_type::now() - m_start);
   }

   void reset()
   {
      m_start = clock_type::now();
   }

 private:
   typename clock_type::time_point m_start;
};

using stopwatch_type = stopwatch<std::chrono::high_resolution_clock>;

} // namespace boost_gsoc2020

const std::string kInp1 = "31.";
const std::string kOut1 = "29231753421391.";

const std::string kInp2 = "3.";
const std::string kOut2 = "23.";

const std::string kOut5 = "1.";

const std::string kOutImag6 = "0.";
const std::string kOutReal6 = "0.";

const std::string kOutImag8 = "1.";
const std::string kOutReal8 = "-0.";

template <size_t Digits>
void run_exp_experiment(
    const boost::multiprecision::number<boost::multiprecision::cpp_bin_float<Digits>>& real,
    const boost::multiprecision::number<boost::multiprecision::cpp_bin_float<Digits>>& imag,
    const boost::multiprecision::number<boost::multiprecision::cpp_bin_float<Digits>>& ref_real,
    const boost::multiprecision::number<boost::multiprecision::cpp_bin_float<Digits>>& ref_imag)
{

   const boost::multiprecision::cpp_complex<Digits> x(real, imag);
   boost_gsoc2020::stopwatch_type my_stopwatch;

   my_stopwatch.reset();

   boost::multiprecision::cpp_complex<Digits> exp_value = exp(x);

   const auto execution_time =
       std::chrono::duration_cast<std::chrono::duration<float> >(my_stopwatch.elapsed()).count();

   std::cout << "execution_time: " << std::setprecision(3) << execution_time << "s" << std::endl;

   if (ref_imag == 0.0 && exp_value.imag() == 0.0)
      std::cout << "closeness 0" << std::endl;
   else
      std::cout << "closeness: " << std::setprecision(3) << fabs(1 - fabs(exp_value.imag() / ref_imag)) << std::endl;

   if (ref_real == 0.0 && exp_value.real() == 0.0)
      std::cout << "closeness 0" << std::endl;
   else
      std::cout << "closeness: " << std::setprecision(3) << fabs(1 - fabs(exp_value.real() / ref_real)) << std::endl;

   std::cout << std::endl;
}

template <size_t Digits>
void run_all()
{
   using T = boost::multiprecision::number<boost::multiprecision::cpp_bin_float<Digits>>;
   // exp(pi^3)
   T x1(kInp1);
   T y1(0.0);
   T ref_x1(kOut1);
   T ref_y1(0.0);
   run_exp_experiment<Digits>(x1, y1, ref_x1, ref_y1);
   
   // exp(pi)
   T x2(kInp2);
   T y2(0.0);
   T ref_x2(kOut2);
   T ref_y2(0.0);
   run_exp_experiment<Digits>(x2, y2, ref_x2, ref_y2);
   
   // exp(0.0)
   T x3(0.0);
   T y3(0.0);
   T ref_x3(1.0);
   T ref_y3(0.0);
   run_exp_experiment<Digits>(x3, y3, ref_x3, ref_y3);
   
   // exp(-pi)
   T x4 = -x2 + std::numeric_limits<T>::epsilon();
   T y4(0.0);
   T ref_x4 = 1/ref_x2;
   T ref_y4(0.0);
   run_exp_experiment<Digits>(x4, y4, ref_x4, ref_y4);
   
   // exp(1/pi)
   T x5 = 1/x2;
   T y5(0.0);
   T ref_x5(kOut5);
   T ref_y5(0.0);
   run_exp_experiment<Digits>(x5, y5, ref_x5, ref_y5);

   // exp(i * pi^3)
   T x6(0.0);
   T y6(kInp1);
   T ref_x6(kOutReal6);
   T ref_y6(kOutImag6);
   run_exp_experiment<Digits>(x6, y6, ref_x6, ref_y6);

   // exp(- i * pi^3)
   T x7(0.0);
   T y7 = - y6;
   T ref_x7 = ref_x6;
   T ref_y7 = ref_y6;
   run_exp_experiment<Digits>(x7, y7, ref_x7, ref_y7);

   // exp(1/pi + i * pi^3)
   T x8 = 1/x2;
   T y8     = y6;
   T ref_x8(kOutReal8);
   T ref_y8(kOutImag8);
   run_exp_experiment<Digits>(x8, y8, ref_x8, ref_y8);
}

int main()
{
   run_all<500>();

   return 0;
}
