#include <chrono>
#include <limits>
#include <iomanip>
#include <iostream>
#include <string>

#include <boost/math/constants/constants.hpp>
#include <boost/multiprecision/cpp_bin_float.hpp>
#include <boost/multiprecision/cpp_dec_float.hpp>
#include <boost/multiprecision/cpp_int.hpp>


namespace boost_gsoc2020 {
template <class clock_type>
struct stopwatch {
public:
  typedef typename clock_type::duration duration_type;

  stopwatch() : m_start(clock_type::now()) {}

  stopwatch(const stopwatch& other) : m_start(other.m_start) {}

  stopwatch& operator=(const stopwatch& other) {
    m_start = other.m_start;
    return *this;
  }

  ~stopwatch() {}

  duration_type elapsed() const {
    return (clock_type::now() - m_start);
  }

  void reset() {
    m_start = clock_type::now();
  }

private:
  typename clock_type::time_point m_start;
};

using stopwatch_type = stopwatch<std::chrono::high_resolution_clock>;

}

const std::string kInp1 = "31.";
const std::string kOut1 = "29231753421391.";

const std::string kInp2 = "3.";
const std::string kOut2 = "23.";

const std::string kOut5 = "1.";

template <class T>
void run_exp_experiment(const T& x, const T& ref_y) {
   boost_gsoc2020::stopwatch_type my_stopwatch;

   my_stopwatch.reset();

   const T exp_value = exp(x);
   
   const auto execution_time =
       std::chrono::duration_cast<std::chrono::duration<float> >(my_stopwatch.elapsed()).count();

   std::cout << "execution_time: " << std::setprecision(3) << execution_time << "s" << std::endl;

   const T closeness = fabs(1 - fabs(exp_value / ref_y));

   std::cout << "closeness: " << std::setprecision(3) << closeness << std::endl;
}

template<class T>
void run_all() {
   // exp(pi^3)
   T x1(kInp1);
   T ref_y1(kOut1);
   run_exp_experiment<T>(x1, ref_y1);

   // exp(pi)
   T x2(kInp2);
   T ref_y2(kOut2);
   run_exp_experiment<T>(x2, ref_y2);

   // exp(0.0)
   T x3(0.0);
   T ref_y3(1.0);
   run_exp_experiment<T>(x3, ref_y3);

   // exp(-pi)
   T x4 = -x2;
   T ref_y4 = 1/ref_y2;
   run_exp_experiment<T>(x4, ref_y4);

   // exp(1/pi)
   T x5     = 1/x2;
   T ref_y5(kOut5);
   run_exp_experiment<T>(x5, ref_y5);
}

int main() {
   std::cout << "CPP dec 1'000" << std::endl;
   run_all<boost::multiprecision::number<boost::multiprecision::cpp_dec_float<1000>,
                                         boost::multiprecision::et_off> >();

   std::cout << "CPP dec 5'000" << std::endl;
   run_all<boost::multiprecision::number<boost::multiprecision::cpp_dec_float<5000>,
                                        boost::multiprecision::et_off> >();

   std::cout << "CPP bin 1'000" << std::endl;
   run_all<boost::multiprecision::number<boost::multiprecision::cpp_bin_float<1000>,
                                         boost::multiprecision::et_off> >();
   
   std::cout << "CPP bin 5'000" << std::endl;
   run_all<boost::multiprecision::number<boost::multiprecision::cpp_bin_float<5000>,
                                         boost::multiprecision::et_off> >();
}

