template <typename T>
void run_sqrt_experiment(
    const T& x,
    const T& ref)
{
   boost_gsoc2020::stopwatch_type my_stopwatch;

   my_stopwatch.reset();

   T exp_value = sqrt(x);

   const auto execution_time =
       std::chrono::duration_cast<std::chrono::duration<float> >(my_stopwatch.elapsed()).count();

   std::cout << "execution_time: " << std::setprecision(3) << execution_time << "s" << std::endl;

   if (ref == 0 && exp_value == 0)
      std::cout << "closeness 0" << std::endl;
   else
      std::cout << "closeness: " << std::setprecision(3) << abs(exp_value - ref) << std::endl;

   std::cout << std::endl;
}

template <typename T>
void run_all()
{
   // sqrt(pi * 10^9999)
   T x1(kInp1);
   T ref_x1(kOut1);
   run_sqrt_experiment<T>(x1, ref_x1);

   // sqrt(pi * 10^10000)
   T x2(kInp2);
   T ref_x2(kOut2);
   run_sqrt_experiment<T>(x2, ref_x2);
   
   for (int i = 0; i < 128; ++i)
   {
      T b = 1;
      b   = b << (2 * i);
      T ref = 1;
      ref   = ref << i;
      if (sqrt(b) != ref)
      {
         std::cout << "Pow i " << i << " is wrong " << std::endl;
      }
   }
   std::cout << "Tested powers 2^i for i = 0, 2, ..., 256" << std::endl;

   T x3(0);
   T ref_x3(0);
   run_sqrt_experiment<T>(x3, ref_x3);

   T x4(1);
   T ref_x4(1);
   run_sqrt_experiment<T>(x4, ref_x4);

   // Triggers the if r < 0 cases.
   T x5(1);
   x5 = (x5 << 10000) - 1;
   T ref_x5(kOut5);
   run_sqrt_experiment<T>(x5, ref_x5);

   // Integers with 1 limb.
   T x6(2306419268LL);
   T ref_x6(sqrt(2306419268LL));
   run_sqrt_experiment<T>(x6, ref_x6);

   T x7(238526429LL);
   T ref_x7(sqrt(238526429LL));
   run_sqrt_experiment<T>(x7, ref_x7);

   // Integers with 2 limbs.
   T x8("1162620140663400369");
   T ref_x8("1078248645");
   run_sqrt_experiment<T>(x8, ref_x8);

   T x9("2664568616535085527");
   T ref_x9("1632350641");
   run_sqrt_experiment<T>(x9, ref_x9);

   // Integers with 3 limbs.
   T x10("26984781090246209473137499070");
   T ref_x10("164270451056318");
   run_sqrt_experiment<T>(x10, ref_x10);

   T x11("15120820810748369907378255947");
   T ref_x11("122966746768174");
   run_sqrt_experiment<T>(x11, ref_x11);

   // Integers with 4 limbs.
   T x12("88743724578984095477373554869009772534");
   T ref_x12("9420388770055304648");
   run_sqrt_experiment<T>(x12, ref_x12);

   T x13("40574055951553183194429630201912946772");
   T ref_x13("6369776758376480433");
   run_sqrt_experiment<T>(x13, ref_x13);

   // Test 2^n-1 which catches the edge cases for overflows. 
   for (int i = 1; i <= 152; ++i)
   {
      T x = 1;
      x   = (x << i) - 1;
      T ref(kOuts[i - 1]);
      if (sqrt(x) != ref)
      {
         std::cout << "Wrong at i = " << i << std::endl;
         std::cout << "Expected : " << kOuts[i - 1] << std::endl;
         std::cout << "Got : " << sqrt(x) << std::endl;
      }
   }
}

int main()
{
   run_all<boost::multiprecision::cpp_int>();
   return 0;
}
